package com.hanwang.taskexecutor.service;import org.slf4j.LoggerFactory;import org.springframework.scheduling.annotation.Async;import org.springframework.scheduling.annotation.AsyncResult;import org.springframework.stereotype.Service;import org.springframework.util.concurrent.ListenableFuture;@Servicepublic class AsyncService {	@Async	public void executeAsync1() {		try {			Thread.sleep(20);			LoggerFactory.getLogger(AsyncService.class).info("异步任务::1");		} catch (InterruptedException e) {			e.printStackTrace();		}	}	@Async	public void executeAsync2() {		LoggerFactory.getLogger(AsyncService.class).info("异步任务::2");	}	@Async	public ListenableFuture<String> sayHello(String name) {		String res = name + ":Hello World!";		LoggerFactory.getLogger(AsyncService.class).info(res);		return new AsyncResult<>(res);	}}