package com.hanwang.web.entity;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableName;import com.baomidou.mybatisplus.extension.activerecord.Model;import com.baomidou.mybatisplus.annotation.TableId;import java.time.LocalDateTime;import com.baomidou.mybatisplus.annotation.TableField;import java.io.Serializable;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.experimental.Accessors;import pub.gadfly.sdk.Handler.ByteArrayToByteaPG;/** * <p> * 用户信息表 实体类 * </p> * * @author gadfly * @since 2019-12-20 */@Data@EqualsAndHashCode(callSuper = true)@Accessors(chain = true)@TableName(value = "user", schema = "swagger2")public class User extends Model<User> {	private static final long serialVersionUID=1L;	@TableId(value = "id", type = IdType.AUTO)	private Integer id;	@TableField("realname")	private String realname;	@TableField("mobile")	private String mobile;	@TableField("email")	private String email;	@TableField("public_key")	private String publicKey;	@TableField("private_key")	private String privateKey;	@TableField("insert_time")	private LocalDateTime insertTime;	@TableField("modify_time")	private LocalDateTime modifyTime;	@TableField(value = "head_pic", typeHandler = ByteArrayToByteaPG.class)	private byte[] headPic;	public static final String ID ="id";	public static final String REALNAME ="realname";	public static final String MOBILE ="mobile";	public static final String EMAIL ="email";	public static final String PUBLIC_KEY ="public_key";	public static final String PRIVATE_KEY ="private_key";	public static final String INSERT_TIME ="insert_time";	public static final String MODIFY_TIME ="modify_time";	public static final String HEAD_PIC ="head_pic";	@Override	protected Serializable pkVal(){		return this.id;	}}